import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function PinPage() {
  const [pin, setPin] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [horaAtual, setHoraAtual] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    atualizarHora();
    const intervalo = setInterval(atualizarHora, 1000);
    return () => clearInterval(intervalo);
  }, []);

  const atualizarHora = () => {
    const agora = new Date();
    setHoraAtual(agora.toLocaleTimeString('pt-BR'));
  };

  const registrarPonto = () => {
    if (!pin) return;

    const funcionarios = JSON.parse(localStorage.getItem('funcionarios') || '[]');
    const funcionario = funcionarios.find(f => f.pin === pin);
    if (!funcionario) {
      setMensagem('PIN inv√°lido. Tente novamente.');
      return;
    }

    const registros = JSON.parse(localStorage.getItem('registros') || '[]');
    const hoje = new Date().toLocaleDateString('pt-BR');
    const registrosHoje = registros.filter(r => r.pin === pin && r.data === hoje);

    const tipo = registrosHoje.length % 2 === 0 ? 'entrada' : 'saida';
    const novaMensagem = tipo === 'entrada' ? `Bom trabalho, ${funcionario.nome}!` : `At√© logo, ${funcionario.nome}!`;

    const novoRegistro = {
      data: hoje,
      horario: new Date().toLocaleTimeString('pt-BR'),
      nome: funcionario.nome,
      pin: pin,
      tipo: tipo
    };

    registros.push(novoRegistro);
    localStorage.setItem('registros', JSON.stringify(registros));
    setMensagem(novaMensagem);
    setPin('');
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-blue-600 text-white px-4">
      <div className="text-center mb-4">
        <h1 className="text-2xl font-bold mb-2">Sistema de Ponto Cristal Acquacenter</h1>
        <div className="text-lg">üïí {horaAtual}</div>
      </div>

      <div className="bg-white text-black rounded-lg shadow p-6 w-full max-w-sm">
        <input
          type="password"
          placeholder="Digite seu PIN"
          value={pin}
          onChange={(e) => setPin(e.target.value)}
          className="w-full p-3 border rounded mb-4"
        />
        <button onClick={registrarPonto} className="w-full bg-blue-700 text-white p-3 rounded hover:bg-blue-800">
          Registrar Ponto
        </button>
      </div>

      {mensagem && (
        <div className="mt-4 bg-black/50 px-4 py-2 rounded text-lg text-white">
          {mensagem}
        </div>
      )}

      <button
        onClick={() => navigate('/login')}
        className="absolute top-4 right-4 bg-gray-800 p-2 rounded-full hover:bg-gray-700"
        title="√Årea Administrativa"
      >
        ‚öôÔ∏è
      </button>
    </div>
  );
}
