import React, { useState, useEffect, useRef } from 'react';
import './AdminPage.css';

function AdminPage({ onVoltar }) {
  const [funcionarios, setFuncionarios] = useState([]);
  const [registros, setRegistros] = useState([]);
  const [novoFuncionario, setNovoFuncionario] = useState({ nome: '', pin: '', foto: '' });
  const [filtroDataInicio, setFiltroDataInicio] = useState('');
  const [filtroDataFim, setFiltroDataFim] = useState('');
  const [mensagem, setMensagem] = useState('');
  const nomeRef = useRef();

  useEffect(() => {
    const funcLocal = JSON.parse(localStorage.getItem('funcionarios')) || [];
    const regLocal = JSON.parse(localStorage.getItem('registros')) || [];
    setFuncionarios(funcLocal);
    setRegistros(regLocal);
    if (nomeRef.current) nomeRef.current.focus();
  }, []);

  const adicionarFuncionario = () => {
    if (!novoFuncionario.nome || !novoFuncionario.pin) {
      setMensagem('Nome e PIN são obrigatórios.');
      setTimeout(() => setMensagem(''), 3000);
      return;
    }
    if (funcionarios.some(f => f.pin === novoFuncionario.pin)) {
      setMensagem('PIN já está em uso.');
      setTimeout(() => setMensagem(''), 3000);
      return;
    }
    const atualizados = [...funcionarios, novoFuncionario];
    setFuncionarios(atualizados);
    localStorage.setItem('funcionarios', JSON.stringify(atualizados));
    setNovoFuncionario({ nome: '', pin: '', foto: '' });
    setMensagem('Funcionário adicionado com sucesso.');
    setTimeout(() => setMensagem(''), 3000);
  };

  const removerFuncionario = index => {
    if (!window.confirm('Remover este funcionário?')) return;
    const atualizados = funcionarios.filter((_, i) => i !== index);
    setFuncionarios(atualizados);
    localStorage.setItem('funcionarios', JSON.stringify(atualizados));
    setMensagem('Funcionário removido.');
    setTimeout(() => setMensagem(''), 3000);
  };

  const editarRegistro = (index, campo, valor) => {
    const atualizados = [...registros];
    atualizados[index][campo] = valor;
    setRegistros(atualizados);
    localStorage.setItem('registros', JSON.stringify(atualizados));
  };

  const removerRegistro = index => {
    if (!window.confirm('Remover este registro?')) return;
    const atualizados = registros.filter((_, i) => i !== index);
    setRegistros(atualizados);
    localStorage.setItem('registros', JSON.stringify(atualizados));
    setMensagem('Registro removido.');
    setTimeout(() => setMensagem(''), 3000);
  };

  const filtrarRegistros = () => {
    const regLocal = JSON.parse(localStorage.getItem('registros')) || [];
    let filtrados = regLocal;
    if (filtroDataInicio) {
      filtrados = filtrados.filter(r => new Date(r.data.split('/').reverse().join('-')) >= new Date(filtroDataInicio));
    }
    if (filtroDataFim) {
      filtrados = filtrados.filter(r => new Date(r.data.split('/').reverse().join('-')) <= new Date(filtroDataFim));
    }
    filtrados.sort((a, b) => new Date(b.data.split('/').reverse().join('-')) - new Date(a.data.split('/').reverse().join('-')));
    setRegistros(filtrados);
  };

  const limparFiltros = () => {
    setFiltroDataInicio('');
    setFiltroDataFim('');
    const regLocal = JSON.parse(localStorage.getItem('registros')) || [];
    regLocal.sort((a, b) => new Date(b.data.split('/').reverse().join('-')) - new Date(a.data.split('/').reverse().join('-')));
    setRegistros(regLocal);
  };

  const imprimir = () => {
    setMensagem('Gerando visualização para impressão...');
    setTimeout(() => {
      setMensagem('');
      window.print();
    }, 1000);
  };

  return (
    <div className="admin-container">
      <h1>Painel Administrativo</h1>
      {mensagem && <div className="mensagem">{mensagem}</div>}

      <div className="funcionario-form">
        <input ref={nomeRef} type="text" placeholder="Nome" value={novoFuncionario.nome} onChange={e => setNovoFuncionario({ ...novoFuncionario, nome: e.target.value })} />
        <input type="text" placeholder="PIN" value={novoFuncionario.pin} onChange={e => setNovoFuncionario({ ...novoFuncionario, pin: e.target.value })} />
        <input type="text" placeholder="URL da Foto" value={novoFuncionario.foto} onChange={e => setNovoFuncionario({ ...novoFuncionario, foto: e.target.value })} />
        <button onClick={adicionarFuncionario}>Adicionar Funcionário</button>
      </div>

      <div className="funcionario-lista">
        <h2>Funcionários</h2>
        <ul>
          {funcionarios.map((f, i) => (
            <li key={i}>
              {f.nome} - PIN: {f.pin}
              <button onClick={() => removerFuncionario(i)}>Remover</button>
            </li>
          ))}
        </ul>
      </div>

      <div className="filtros">
        <input type="date" value={filtroDataInicio} onChange={e => setFiltroDataInicio(e.target.value)} />
        <input type="date" value={filtroDataFim} onChange={e => setFiltroDataFim(e.target.value)} />
        <button onClick={filtrarRegistros}>Buscar</button>
        <button onClick={limparFiltros}>Limpar</button>
      </div>

      <div className="tabela-registros">
        <h2>Registros</h2>
        <table>
          <thead>
            <tr>
              <th>Nome</th>
              <th>Data</th>
              <th>Hora</th>
              <th>Tipo</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {registros.map((r, i) => (
              <tr key={i} className={i % 2 === 0 ? 'bg-gray-100' : 'bg-white'}>
                <td>{r.nome}</td>
                <td><input value={r.data} onChange={e => editarRegistro(i, 'data', e.target.value)} /></td>
                <td><input value={r.hora} onChange={e => editarRegistro(i, 'hora', e.target.value)} /></td>
                <td>
                  <select value={r.tipo} onChange={e => editarRegistro(i, 'tipo', e.target.value)}>
                    <option>Entrada</option>
                    <option>Saída</option>
                  </select>
                </td>
                <td><button onClick={() => removerRegistro(i)}>Excluir</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="botoes">
        <button onClick={imprimir}>Imprimir</button>
        <button onClick={onVoltar}>Voltar</button>
      </div>
    </div>
  );
}

export default AdminPage;
